using System.Collections; using System.Collections.Generic; using UnityEngine;  public class SkyboxCamera : MonoBehaviour {  	private const float ROTATION_SPEED = 180f;     public float ROTATION_DEMULTIPLIER;      public void OnAcceleration(float x,float y)     {         Quaternion desiredRotation = Quaternion.Euler(90 * y * ROTATION_DEMULTIPLIER, 0, 90 * x * ROTATION_DEMULTIPLIER);         Quaternion originalRotation = transform.rotation;                  transform.rotation = Quaternion.Slerp(originalRotation, desiredRotation, Time.deltaTime * ROTATION_SPEED);     } } 